mapper.insert:
insert 
  into board
values(
            seq_nbno.nextval
          , #{ memNo}  
          , #{ boardWriter }
          , 1
		  , #{ boardTitle }
		  , #{ boardContent }
          , sysdate
          , 0
          , default
		)

mapper.update:
update
	   board
   set board_title = #{boardTitle}
     , board_content = #{boardContent}
 where board_no = #{boardNo}

update
	   board_file
   set origin_name = #{originName}
     , change_name = #{changeName}
 where ref_bno = #{refBno}

