mapper.insert:
	insert
	  into document
	  (
	    doc_no
	  , doc_writer
	  , doc_content
	  , doc_category
	  , create_date
	  , origin_name
	  , change_name
	  , status
	  )
	 values
 	   (
 	     seq_docno.nextval
 	   , #{docWriter}
 	   , #{docContent}
 	   , #{docCategory}
 	   , sysdate
 	   , #{originName}
 	   , #{changeName}
 	   , 'Y'
 	   )
