mapper.insert:
		insert 
		  into market
		values(
		            seq_mkno.nextval
		          , #{ memNo}  
		          , #{ marketWriter }
				  , #{ marketTitle }
				  , #{ marketContent }
		          , sysdate
		          , '판매중'
		          , #{ price }
                  , #{ productImg }
                  , 0
                  , 0
                  , 'Y'
				)

mapper.update:
update
	   market
   set market_title = #{marketTitle}
     , market_content = #{marketContent}
     , p_img = #{productImg}
 where market_no = #{marketNo}

