mapper.insert:
		insert 
		  into market
		values(
		            seq_mkno.nextval
		          , #{ memNo}  
		          , #{ marketWriter }
				  , #{ marketTitle }
				  , #{ marketContent }
		          , sysdate
		          , '판매중'
		          , #{ price }
                  , #{ productImg }
                  , 0
                  , 0
                  , 'Y'
				)

mapper.update:
update
	   market
   set market_title = #{marketTitle}
     , market_content = #{marketContent}
     , p_img = #{productImg}
 where market_no = #{marketNo}

mapper.insert:
	insert
		  into reply
		  values
		  (
		   	seq_rno.nextval
          , #{ memNo }
          , '벼룩'
          , #{refNo} 
		  , #{replyContent}
		  , #{replyWriter}
		  , sysdate 
          , default
		  )

		insert
		  into report
              (
                rp_code
              , rp_id
              , board_type
              , rp_type
              , rp_no
              , rp_section
              , rp_content
              )
			  values
			  (
			   	seq_brpno.nextval
	          , #{rpId}
	          , #{boardType}
	          , #{rpType} 
			  , #{rpNo}
			  , #{rpSection}
              , #{rpContent}
			  )

insert 
  into p_like
     (
       like_no
     , market_no
     , mem_id  
     )
 values
 	 (
 	   seq_lkno.nextval
 	 , #{marketNo}
 	 , #{memId}    
 	 )

mapper.delete:
	delete
     from p_like
    where market_no = #{marketNo}
      and mem_id = #{memId}

mapper.update:
        update market
           set p_like = (select count(*)
        	   			   from p_like
        	   			   where market_no = #{marketNo} 
				         ) 
         where market_no = #{marketNo}

mapper.select:
select count(*)
from p_like
 where market_no = #{marketNo}
   and mem_id = #{memId}
