mapper.update:
update
       message


		   set to_recycle_bin = 'N'


		   set from_recycle_bin = 'N'				


 where msg_no in ( 

		${msgNo}

	)


	    	and recipient_no = ${memNo}


	    	and sender_no = ${memNo}

	update
	       message
			   set to_recycle_bin = 'Y'
	 where msg_no in ( 

			${msgNo}

		)
       and recipient_no = ${memNo}

	update
	       message
			   set from_recycle_bin = 'Y'
	 where msg_no in ( 

			${msgNo}

		)
       and sender_no = ${memNo}

	update
	       message
			   set to_del_status = 'N'
	 where msg_no in ( 

			${msgNo}

		)
       and recipient_no = ${memNo}

	update
	       message
			   set from_del_status = 'N'
	 where msg_no in ( 

			${msgNo}

		)
       and sender_no = ${memNo}

 		update
		       message
				   set read_status = 'Y'
		 where msg_no = ${msgNo}
	       and recipient_no = ${memNo}

mapper.insert:
	 	insert 
	 	  into Message
	 	  (
	 	  	msg_no
	 	  , recipient_no	
	      , sender_no
	  	  , msg_status
		  , content_status
		  , msg_content
	 	  )
	 	  values
	 	  (
	 	    seq_msgno.nextval
	 	  , #{ RecipientMemNo } 
  	 	  , #{ senderMemNo } 
  	 	  , #{ msgStatus } 
  	 	  , #{ contentStatus } 
  	 	  , #{ msgContent } 
	 	  )
