mapper.insert:
insert
  into reservation
  	   (
  	     rez_no
  	   , resource_name
  	   , rez_writer
  	   , use_for
  	   , rez_date
  	   , rez_starttime
  	   , rez_endtime
  	   , apply_date
  	   , status
  	   )
 values
 	   (
 	     seq_rreno.nextval
 	   , #{resourceName}
 	   , #{rezWriter}
 	   , #{useFor}
 	   , #{rezDate}
 	   , #{startTime}
 	   , #{endTime}
 	   , sysdate
 	   , 'Y'
 	   )

mapper.update:
update reservation
   set 
   	   resource_name = #{resourceName}
   	 , use_for 		 = #{useFor}
   	 , rez_date 	 = #{rezDate}
   	 , rez_starttime = #{startTime}
   	 , rez_endtime   = #{endTime}
   	 , apply_date 	 = sysdate
 where 
 	   rez_no = #{rezNo}

update reservation
   set status = 'N'
 where rez_no = #{rezNo}
