mapper.insert:
insert 
  into schedule
  (
    sc_no
  , sc_writer
  , sc_title
  , sc_content
  , sc_type
  , sc_start
  , sc_end
  , allday
  , status
  )
 values
 (
   seq_scno.nextval
 , #{scWriter}
 , #{title}
 , #{scContent}
 , #{scType}
 , #{start}
 , #{end}
 , #{allday}
 , 'Y'
 )

insert
  into biz_report
  	   (
  	   	 sc_no
  	   , report_writer
  	   , report_title
  	   , report_content
  	   , origin_name
  	   , change_name
  	   , create_date
  	   , status
  	   )
 values
 	   (
 	     #{scNo}
 	   , #{reportWriter}
 	   , #{reportTitle}
 	   , #{reportContent}
 	   , #{originName}
 	   , #{changeName}
 	   , sysdate
 	   , 'Y'
 	   )

mapper.update:
update schedule
   set 
       sc_writer = #{scWriter}
     , sc_title = #{title}
     , sc_content = #{scContent}
     , sc_type = #{scType}
     , sc_start = #{start}
     , sc_end = #{end}
     , allday = #{allday}
 where sc_no = #{scNo}
